<script setup>
import { onMounted, onBeforeUnmount } from "vue";
import gsap from "gsap";

const letterAnimation = () => {
	const letters = document.querySelectorAll(".letter");

	letters.forEach((letter, index) => {
		gsap.to(letter, {
			duration: 0.3,
			delay: index * 0.1,
			fill: "#7e250c",
			repeat: 1,
			yoyo: true,
			ease: "power1.inOut",
		});
	});
};

const dragonAnimationX = () => {
	gsap.to(".dragon", {
		x: "220%",
		duration: 7,
		repeat: -1,
		yoyo: true,
		ease: "power1.inOut",
	});
};

const dragonAnimationY = () => {
	gsap.to(".dragon", {
		y: "10%",
		duration: 0.5,
		repeat: -1,
		yoyo: true,
		ease: "power1.inOut",
	});
};

let flipped = false;

setInterval(() => {
	flipped = !flipped;
	gsap.to(".dragon", {
		scaleX: flipped ? -1 : 1,
		duration: 0.5,
		ease: "power2.inOut",
		onStart: () => {
			if (flipped) {
				gsap.to(".dragon", { zIndex: 1, duration: 0 }); // Bakom slottet
			} else {
				gsap.to(".dragon", { zIndex: -1, duration: 0 }); // FramfÃ¶r slottet
			}
		},
	});
}, 7000);

onMounted(() => {
	letterAnimation();
	dragonAnimationX();
	dragonAnimationY();

	const intervalId = setInterval(letterAnimation, 5000);

	onBeforeUnmount(() => {
		clearInterval(intervalId);
	});
});
</script>

