<script setup>
import { onMounted, onBeforeUnmount } from "vue";
import gsap from "gsap";

const startAnimation = () => {
	const letters = document.querySelectorAll(".letter"); // Assuming all letters share the 'letter' class

	letters.forEach((letter, index) => {
		gsap.to(letter, {
			duration: 0.3,
			delay: index * 0.1, // Delay based on the index
			fill: "#7e250c", // Darker color for pulsing
			repeat: 1, // Infinite repetition
			yoyo: true, // Alternate between original and pulsed state
			ease: "power1.inOut", // Ease for smooth pulsing
		});
	});
};

onMounted(() => {
	startAnimation(); // Start animation when the component is mounted

	// Run the animation every 5 seconds
	const intervalId = setInterval(startAnimation, 5000);

	// Clean up interval on component unmount
	onBeforeUnmount(() => {
		clearInterval(intervalId);
	});
});
</script>

